import { Router } from 'express';
import AppController from '../controllers/AppController.js';
import UsersController from '../controllers/UsersController.js'; 
import AuthController from '../controllers/AuthController.js'; 
import FilesController from '../controllers/FilesController.js';

const router = Router();

// App status and stats routes
router.get('/status', AppController.getStatus); // Check server status
router.get('/stats', AppController.getStats);   // Get application statistics

// User registration and authentication routes
router.post('/users', UsersController.postNew);                // Register a new user
router.get('/users/me', UsersController.getMe);               // Get current user's information
router.get('/connect', AuthController.getConnect);            // Connect a user
router.get('/disconnect', AuthController.getDisconnect);      // Disconnect a user

// File management routes
router.post('/files', FilesController.postUpload);            // Upload a file
router.get('/files', FilesController.getIndex);               // List all files
router.get('/files/:id', FilesController.getShow);            // Get a specific file by ID
router.get('/files/:id/data', FilesController.getData);       // Get the file data (updated to match the method name)

// File visibility management routes
router.put('/files/:id/publish', FilesController.putPublish);  // Publish a file
router.put('/files/:id/unpublish', FilesController.putUnpublish); // Unpublish a file

export default router;

